SELECT CNT
FROM PRODUCT_OPTION
WHERE PRODUCT_NUM = #{productNum}
AND OPTION_NAME = #{productOption}

SELECT LISTAGG(OPTION_NAME, ',') WITHIN GROUP(ORDER BY OPTION_NAME) AS OPTION_NAME
FROM PRODUCT_OPTION
WHERE PRODUCT_NUM = #{productNum}

